.stats-wrap{
  margin-top: 20px;
  justify-content: space-between;
  .statsItem{
    border: 1px solid #000;
    &:not(:last-of-type){
      margin-right: 15px;
    }
    .statsItem-title{
      background: #ddd;
      padding: 10px 20px;
      line-height: 20px;
    }
    .statsItem-content{
      text-align: center;
      padding: 10px;
      >*:not(:last-of-type){
        margin-bottom: 10px;
      }
      .item-row{
        justify-content: center;
        :not(.item-label){
          flex: 1 1 auto;
          text-align: left;
        }
      }
      .item-label{
        flex: 0 0 40%;
        text-align: right;
        margin-right: 5px;
      }
      .stats-count{
        font-size: 20px;
      }
    }
  }
}
