import { agreeDesc } from '@/api/contentMgr'
import { autoCheckText, checkedText } from '@/utils/const'
export default {
  props: {
    userInfo: {
      type: Object,
      default: () => ({})
    }
  },
  computed: {
    isChecked() {
      return this.userInfo.desc === autoCheckText
    }
  },
  methods: {
    async agreeCheck() {
      try {
        const { userId } = this.userInfo
        const desc = this.userInfo.checkDesc
        await agreeDesc({ userId, desc })
        this.$emit('emitFunc', { function: 'initModule', arguments: [] })
      } catch (err) {
        err !== 'cancel' && console.error(err)
      }
    },
    async failCheck() {
      try {
        await this.$msgbox.confirm(`确定要进行该操作`, `删除活动标题`, {
          confirmButtonText: '删除',
          cancelButtonText: '取消',
          type: 'warning'
        })
        const { userId } = this.userInfo
        const desc = checkedText
        await agreeDesc({ userId, desc })
        this.$emit('emitFunc', { function: 'initModule', arguments: [] })
      } catch (err) {
        err !== 'cancel' && console.error(err)
      }
    }
  }
}
